sources.styles/components/_logo.scss:
@use 'abstracts' as *;

.logo {
   @include m(pm) {
      display: block;
      width: 150px;
      margin-left: 20px;
   }
}

sources.styles/components/_hamburger.scss:
@use 'abstracts' as *;

.hamburger {
   @include m(btn) {
      display: none;
      @include mq(max-width, tablet) {
         display: block;
         @include position(absolute, $z-index: 9998) {
            top: 1.8rem;
            right: 20px;
         }
      }
   }
}

sources.styles/components/_title.scss:
@use 'abstracts' as *;

.title {
   @include m(pm) {
      margin-bottom: 1rem;
   }

   @include m(sd) {
      margin-bottom: 1.5rem;
   }

   @include m(services) {
      position: relative;
      @include p-el(before, $width: 30%, $height: 3px) {
         @include position(absolute) {
            background-color: white;
            bottom: -20px;
            left: 50%;
            transform: translateX(-50%);
         }
