sources.styles/components/_service.scss:
@use 'abstracts' as *;

.service {
   @include flexy($dir: column, $align: center);
   gap: 0.7rem;
   & .desc {
      width: 90%;
      max-width: 600px;
   }
}

sources.styles/components/_button.scss:
@use 'abstracts' as *;

.btn {
   display: block;
   width: 50%;
   max-width: 300px;
   padding: 0.6em 2em 0.7em;
   margin-inline: auto;
   transition: opacity var(--hover-transition);
   @include uc();
   &:hover {
      opacity: 0.7;
   }

   @include m(work) {
      margin-top: 3.5rem;
   }

   @include m(cta) {
      @include mq(min-width, tablet) {
         margin-inline: unset;
      }
   }
}

sources.styles/layout/_header.scss:
@use 'abstracts' as *;

.header {
   @include m(pm) {
      padding: var(--ct-padding-header);
      position: relative;

      @include mq(min-width, tablet) {
         @include flexy($align: center, $just: space-between);
         padding-right: 20px;
      }
   }
}

sources.styles/layout/_sidebar.scss:
@use 'abstracts' as *;

.sidebar {
   @include mq(max-width, tablet) {
      background-color: hsl(var(--clr-soft-white));
      @include position(fixed, $z-index: 9997) {
         inset: 0 0 0 calc(100% - 300px);
         width: 300px;
         padding: 4rem 0 min(5rem, 15vh);
      }
      transform: translateX(100%);
      transition: transform var(--sidebar-transition);
   }
}

sources.styles/layout/_nav.scss:
@use 'abstracts' as *;

.nav {
   & a {
      color: hsl(var(--clr-text-pale-blue));
      @include mq(max-width, tablet) {
         display: block;
         color: hsl(var(--clr-pm-dark-blue));
         &:hover {
            color: hsl(var(--clr-text-blue));
         }
      }
      transition: color var(--hover-transition);
   }

   @include e(list) {
      @include flexy($dir: column);
      gap: 1rem;
      @include mq(min-width, tablet) {
         @include flexy($dir: row);
      }
   }
}

sources.styles/layout/_intro.scss:
@use 'abstracts' as *;

.intro {
   padding: var(--ct-padding-intro);

   @include e(body) {
      @include flexy($dir: column, $align: center);

      gap: 1.7rem;
      @include mq(min-width, tablet) {
         @include flexy($dir: row);
         gap: 1.5rem;
      }

      @include mq(min-width, desktop) {
         gap: 0;
      }

      & > * {
         flex: 1 100%;
         // outline: 1px solid black;
      }
   }
}

sources.styles/layout/_services.scss:
@use 'abstracts' as *;

.services {
   padding: var(--ct-padding-services);

   @include e(body) {
      @include flexy($dir: column);
      gap: 2.5rem;
      margin-top: 3rem;
      @include mq(min-width, desktop) {
         @include flexy($dir: row);
      }
   }
}

sources.styles/layout/_cta.scss:
@use 'abstracts' as *;

.cta {
   padding: var(--ct-padding-cta);

   @include e(body) {
      @include flexy($dir: column, $align: center);
      gap: 2rem;
      @include mq(min-width, tablet) {
         @include flexy($dir: row, $just: space-between);
      }
   }

   @include e(arrow) {
      width: 30px;
      @include mq(min-width, tablet) {
         transform: rotate(90deg);
         margin-right: 2.2rem;
      }
   }
}

sources.styles/utilities/_container.scss:
@use 'abstracts' as *;

.container-wrapper {
   // outline: 1px solid black;
   width: 90%;
   max-width: 1000px;
   margin-inline: auto;
}

sources.styles/utilities/_sr-only.scss:
@use 'abstracts' as *;

.sr-only {
   position: absolute;
   width: 1px;
   height: 1px;
   padding: 0;
   margin: -1px;
   overflow: hidden;
   clip: rect(0, 0, 0, 0);
   white-space: nowrap;
   border: 0;
}

