         inset: 0 0 0 calc(100% - 300px);
         width: 300px;
         padding: 4rem 0 min(5rem, 15vh);
      }
      //   transform: translateX(100%);
      transition: transform var(--sidebar-transition);
   }
}

sources.styles/layout/_nav.scss:
@use 'abstracts' as *;

.nav {
   & a {
      color: hsl(var(--clr-text-pale-blue));
      @include mq(max-width, tablet) {
         display: block;
         color: hsl(var(--clr-pm-dark-blue));
         &:hover {
            color: hsl(var(--clr-text-blue));
         }
      }
      transition: color var(--hover-transition);
   }

   @include e(list) {
      @include flexy($dir: column);
      gap: 1rem;
   }
}

sources.styles/utilities/_container.scss:
