.container-wrapper {
   outline: 2px solid black;
   width: 90%;
   max-width: 1000px;
   margin-inline: auto;
}

sources.styles/utilities/_sr-only.scss:
@use 'abstracts' as *;

.sr-only {
   position: absolute;
   width: 1px;
   height: 1px;
   padding: 0;
   margin: -1px;
   overflow: hidden;
   clip: rect(0, 0, 0, 0);
   white-space: nowrap;
   border: 0;
}

