         } @else if $feature == line-height {
            @each $property, $value in $nested-map {
               .#{$property} {
                  line-height: var(--#{$property});
               }
            }
         } @else if $feature == weight {
            @each $property, $value in $nested-map {
               .#{$property} {
                  font-weight: var(--#{$property});
               }
            }
         } @else {
            @each $property, $value in $nested-map {
               .#{$property} {
                  letter-spacing: var(--#{$property});
               }
            }
         }
      }
   }
}

