         align-items: center;
         & .title--sd {
            max-width: 350px;
         }
      }
      @include e(line) {
         @include mq(min-width, tablet) {
            height: 1px;
            width: 100%;
            border-top: var(--thin-line);
         }
      }
   }
}

sources.styles/layout/_footer.scss:
@use 'abstracts' as *;

.footer {
   @include m(pm) {
      padding-block: var(--ct-padding-footer);
      @include e(body) {
         @include mq(max-width, tablet) {
            display: grid;
            place-items: center;
            gap: 1rem;
         }
         @include mq(min-width, tablet) {
            @include flexy($just: space-between, $align: center);
         }
      }
   }
}

sources.styles/layout/_hero.scss:
@use 'abstracts' as *;

.hero {
   @include m(home) {
      margin: 1rem 0 7rem;

      @include e(body) {
         grid-template-columns: 1fr;
         @include mq(min-width, tablet) {
            grid-template-columns: repeat(5, 1fr);
            @include bg-img(
               '../assets/images/homepage/desktop/hero.jpg',
               $position: -210px center,
               $size: 130%
            );

            @include e(img) {
               grid-column: 1 / -1;
               grid-row: 1 / -1;
               opacity: 0;
            }

            @include e(txt) {
               grid-column: 3 / 6;
               grid-row: 1 / -1;
               align-self: flex-end;
               padding: 1rem 1rem 0 0;
               @include flexy($dir: column, $align: flex-end);
               & .title {
                  max-width: 500px;
                  margin-bottom: 2rem;
                  text-align: right;
               }

               @include mq(min-width, desktop) {
                  grid-column: 3 / -1;
               }
            }
         }
         @include mq(min-width, desktop) {
            grid-template-columns: repeat(4, 1fr);
         }
      }
   }

   @include m(details) {
      margin-bottom: var(--details-hero-ct);
   }
}

sources.styles/layout/_form.scss:
@use 'abstracts' as *;

.form {
   @include e(fieldset) {
      margin-bottom: 1.3rem;
   }

   @include e(label) {
      display: block;
      margin-bottom: 1rem;
   }
}

sources.styles/utilities/_container.scss:
