sources.styles/pages/_home.scss:
@use 'abstracts' as *;

.about {
   @include e(body) {
      @include e(img) {
         transform: rotateY(180deg);
      }

      @include mq(min-width, tablet-home) {
         gap: var(--gap-offset);
         @include e(img) {
            @include bg-img(
               '../assets/images/homepage/desktop/about.jpg',
               $position: 75%,
               $size: cover
            );
            & > img {
               display: none;
            }
         }
      }

      @include mqs(min-width, tablet-home, max-width, desktop) {
         grid-template-columns: 320px auto;
         grid-template-rows: minmax(630px, auto);
         @include e(img) {
            background-position: 72% 100%;
         }
      }

      @include mq(min-width, desktop) {
         // grid-template-columns: minmax(480px, auto) auto;
         @include e(img) {
            background-position: 90%;
         }
      }

      @include e(txt) {
         padding: 2rem 0 3.5rem;
         & .desc {
            @include mq(max-width, tablet) {
               margin-bottom: 2rem;
            }
            @include mq(min-width, desktop) {
               max-width: 370px;
               margin-bottom: 3rem;
            }
         }
      }
   }
}

sources.styles/pages/_portfolio.scss:
@use 'abstracts' as *;

.project {
   @include e(img) {
      & > a {
         transition: transform 0.3s ease-in-out;
         &:hover {
            transform: translateY(-3%);
         }
      }
   }

   @include mq(min-width, tablet) {
      gap: var(--gap-offset);
      @include e(img) {
         display: grid;
         place-items: center;
      }
   }

   @include mqs(min-width, tablet, max-width, desktop) {
      grid-template-columns: repeat(2, 1fr);
   }

   @include e(txt) {
      @include e(links) {
         @include flexy($align: center);
         flex-wrap: wrap;
         gap: 1rem;
      }

      @include mq(max-width, tablet) {
         padding-block: 1.7rem;
         & .desc {
            margin-bottom: 4rem;
         }
      }

      @include mq(min-width, desktop) {
         & .desc {
            margin-bottom: 2rem;
         }
      }
   }

   @include m(right) {
      @include mq(min-width, tablet) {
         & .project__img {
            grid-column: 2 / 3;
            grid-row: 1 / -1;
         }
         & .project__txt {
            grid-column: 1 / 2;
            grid-row: 1 / -1;
         }
      }

      @include mq(min-width, desktop) {
         grid-template-columns: auto minmax(500px, 1fr);
      }
   }
}

sources.styles/pages/_contact.scss:
@use 'abstracts' as *;

.grid-ct {
   @include m(contact) {
      padding: var(--ct-contact);
      gap: var(--gap-contact);
      @include mq(min-width, desktop) {
         grid-template-columns: repeat(3, 1fr);
      }
   }
}

.get-in-touch {
   @include e(body) {
      & .desc {
         margin-bottom: var(--contact-get-in-touch);
      }

      @include e(info) {
         margin-bottom: 1.5rem;
         & strong {
            padding-right: 0.5rem;
         }
         & a {
            color: inherit;
            &:hover {
               text-decoration: underline;
            }
         }
      }

      @include mq(min-width, desktop) {
         grid-column: 2 / -1;
      }
   }
}

.contact-me {
   margin-bottom: 4rem;
   @include e(title) {
      & .title {
         @include mq(min-width, tablet) {
            margin-bottom: 3rem;
         }
      }
   }

   @include e(form) {
      @include mq(min-width, desktop) {
         grid-column: 2 / -1;
      }
   }
}

sources.styles/pages/_details.scss:
@use 'abstracts' as *;

.details {
   // body tag
   @include e(body) {
      gap: var(--gap-details);
      margin-bottom: 6rem;

      @include mq(min-width, desktop) {
         grid-template-columns: repeat(5, 1fr);
         grid-template-rows: repeat(4, auto);

         @include e(background) {
            grid-column: 3 / -1;
         }
      }

      @include e(hero) {
         align-content: center;
         justify-items: left;
         padding: var(--ct-padding-details-hero);
         & .desc {
            margin-bottom: var(--details-hero-desc);
            @include mqs(min-width, tablet, max-width, desktop) {
               grid-column: 2 / -1;
               grid-row: 1 / 4;
            }
         }

         @include e(skills) {
            max-width: 350px;
         }

         @include mqs(min-width, tablet, max-width, desktop) {
            grid-template-columns: repeat(2, 1fr);
            --gap: 1.2rem 3rem;
         }

         @include mq(min-width, desktop) {
            grid-column: 1 / 3;
            grid-row: 1 / 3;
         }
      }

      @include e(background) {
         & h2 {
            margin-bottom: 2rem;
         }
         @include mq(min-width, desktop) {
            grid-row: 1 / 3;
         }
      }

      @include e(previews) {
         --gap: 2.5rem;
         @include mq(min-width, desktop) {
            grid-column: 3 / -1;
            grid-row: 2 / -1;
         }
      }
   }
}
